var id = current.sys_id.toString();
var record = new GlideRecord('sc_req_item');
record.addQuery('request', id);
record.query();

while(record.next()){
	workflow.info(record.cat_item.toString());
	var itemID = record.cat_item.toString();
	var trailer = new GlideRecord('x_414100_project_3_trailer_catalog_table');
	if(trailer.get(itemID)){
		//request is for a trailer
		workflow.scratchpad.isTrailer = true;
		workflow.info('should have trailer');
	}
	else{
		workflow.scrathpad.isTrailer = false;
		//request is not for a trailer
	}
}

workflow.info('isTrailer = ' + workflow.scratchpad.isTrailer);

